import{_ as i,c as e,o as l,a as t}from"./app.471d11cf.js";const y=JSON.parse('{"title":"几种隐藏元素的方式比较","description":"","frontmatter":{"isTimeLine":true,"title":"几种隐藏元素的方式比较","date":"2020-05-03T00:00:00.000Z","tags":["面试","CSS"],"categories":["面试"]},"headers":[{"level":2,"title":"display","slug":"display","link":"#display","children":[]},{"level":2,"title":"visibility","slug":"visibility","link":"#visibility","children":[]},{"level":2,"title":"opacity","slug":"opacity","link":"#opacity","children":[]}],"relativePath":"pages/interview/css/hideElement.md","lastUpdated":1678348653000}'),a={name:"pages/interview/css/hideElement.md"},s=t('<h1 id="几种隐藏元素的方式比较" tabindex="-1">几种隐藏元素的方式比较 <a class="header-anchor" href="#几种隐藏元素的方式比较" aria-hidden="true">#</a></h1><ul><li>display</li><li>opacity</li><li>visibility</li></ul><h2 id="display" tabindex="-1">display <a class="header-anchor" href="#display" aria-hidden="true">#</a></h2><blockquote><p>display:none</p></blockquote><ul><li>元素将不再占据页面空间,变为&quot;不可见&quot;节点</li><li>触发回流,进而触发重绘</li></ul><h2 id="visibility" tabindex="-1">visibility <a class="header-anchor" href="#visibility" aria-hidden="true">#</a></h2><blockquote><p>visibility:hidden</p></blockquote><ul><li>直接隐藏元素,但仍占据页面空间</li><li>触发重绘</li><li>绑定的事件不能被触发</li></ul><h2 id="opacity" tabindex="-1">opacity <a class="header-anchor" href="#opacity" aria-hidden="true">#</a></h2><blockquote><p>opacity:0</p></blockquote><ul><li>使元素变为透明,仍占据页面空间</li><li>触发重绘</li><li>绑定的事件仍然能被触发</li></ul>',11),d=[s];function o(c,r,n,h,p,u){return l(),e("div",null,d)}const b=i(a,[["render",o]]);export{y as __pageData,b as default};
