import{_ as e,c as i,o as a,a as l}from"./app.ad5e6bd3.js";const f=JSON.parse('{"title":"小程序特点","description":"","frontmatter":{"isTimeLine":true,"title":"小程序特点","date":"2020-04-14T00:00:00.000Z","tags":["面试","小程序"],"categories":["面试"]},"headers":[{"level":2,"title":"引流能力强","slug":"引流能力强","link":"#引流能力强","children":[]},{"level":2,"title":"降低流失率","slug":"降低流失率","link":"#降低流失率","children":[]},{"level":2,"title":"推广迅速","slug":"推广迅速","link":"#推广迅速","children":[]},{"level":2,"title":"服务好","slug":"服务好","link":"#服务好","children":[]}],"relativePath":"pages/interview/mini/feature.md","lastUpdated":1679313190000}'),t={name:"pages/interview/mini/feature.md"},r=l('<h1 id="小程序特点" tabindex="-1">小程序特点 <a class="header-anchor" href="#小程序特点" aria-hidden="true">#</a></h1><h2 id="引流能力强" tabindex="-1">引流能力强 <a class="header-anchor" href="#引流能力强" aria-hidden="true">#</a></h2><ul><li>在微信中,小程序拥有多个入口</li><li>使用场景丰富,能涵盖大多数App的需求</li></ul><h2 id="降低流失率" tabindex="-1">降低流失率 <a class="header-anchor" href="#降低流失率" aria-hidden="true">#</a></h2><ul><li>app现在动不动就是几十M,安装后加上缓存迅速就能达到100M占用</li><li>在小程序之前,每个产品都有自己的app,用户需要经历下载-&gt;安装-&gt;注册等环节之后,才能使用</li><li>手机空间不足时,就会导致用户卸载一部分最近使用频率低的app,很容易会导致用户流失</li><li>小程序,无需下载/安装，只需要轻轻一点就可以使用。使用完成后，点击关闭即可。不会占用内存空间，也就不会存在卸载的问题</li><li>用户使用过一次之后，小程序还会默默的“躺在”曾经使用过的小程序中，这样以来就可以降低因用户卸载而带来的流失率</li></ul><h2 id="推广迅速" tabindex="-1">推广迅速 <a class="header-anchor" href="#推广迅速" aria-hidden="true">#</a></h2><ul><li>小程序基于某个特定的平台(微信/支付宝/头条/...),这些平台往往本身就拥有庞大的流量</li><li>线下推广做活动只需扫一扫小程序码即可使用,没有更多的繁琐操作</li><li>容易传播,小程序可以像消息一样转发、分享给好友/群</li></ul><h2 id="服务好" tabindex="-1">服务好 <a class="header-anchor" href="#服务好" aria-hidden="true">#</a></h2><ul><li>小程序与微信公众号结合,可以直接为用户主动推送一些优惠活动消息</li><li>小程序提供了用户反馈渠道,无需应用内置开发此功能</li></ul><div class="tip custom-block"><p class="custom-block-title">参考</p><p><a href="https://zhidao.baidu.com/question/461407080721331845.html" target="_blank" rel="noreferrer">百度知道:小程序的特点</a></p></div>',10),n=[r];function s(d,h,c,o,u,p){return a(),i("div",null,n)}const m=e(t,[["render",s]]);export{f as __pageData,m as default};
